package com.programmersgateway.sm1999.covid19sahayak;import android.content.Intent;import android.graphics.drawable.ColorDrawable;import android.net.Uri;import android.os.Bundle;import android.view.MenuItem;import android.view.View;import android.view.Window;import android.widget.Button;import androidx.annotation.NonNull;import androidx.appcompat.app.ActionBarDrawerToggle;import androidx.appcompat.app.AppCompatActivity;import androidx.drawerlayout.widget.DrawerLayout;import com.google.android.material.button.MaterialButton;import com.google.android.material.navigation.NavigationView;public class ErrorActivity extends AppCompatActivity {    private Button btnTry;    SharedPrefs sharedPrefs;    MaterialButton contact_helpline;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        overridePendingTransition(R.anim.fadein, R.anim.fadeout);        sharedPrefs = new SharedPrefs(this);        if(sharedPrefs.loadNightModeState())        {            setTheme(android.R.style.ThemeOverlay_Material_Dark);            getSupportActionBar().setBackgroundDrawable(new ColorDrawable(getResources().getColor(R.color.darkPrimary)));            Window window = getWindow();            window.setStatusBarColor(getResources().getColor(R.color.darkPrimaryDark));        }        else            setTheme(R.style.AppTheme);        setContentView(R.layout.activity_error);        btnTry = findViewById(R.id.btnTry);        contact_helpline = findViewById(R.id.contact_helpline);        btnTry.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                startActivity(new Intent(ErrorActivity.this, DashboardActivity.class));                finish();            }        });        contact_helpline.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                startActivity(new Intent(ErrorActivity.this, TollFreeNumberActivity.class));                finish();            }        });    }    @Override    public void onBackPressed() {        super.onBackPressed();        finish();    }    @Override    protected void onDestroy() {        super.onDestroy();    }}